document.addEventListener("DOMContentLoaded",(function(){loadData("profile.json").then((a=>{let o;$(".dashboard-profile--link").href(a.html_url),$(".dashboard-profile--avatar").src(a.avatar_url),$(".dashboard-profile--name").text(a.name||a.login),$(".dashboard-profile--login").text("@"+a.login),$(".dashboard-profile--bio").text(a.bio||""),(o=a.location)?$(".dashboard-profile--location span").text(o):$(".dashboard-profile--location").hide(),(o=a.blog)?$(".dashboard-profile--blog a").link(o):$(".dashboard-profile--blog").hide(),(o=a.email)?$(".dashboard-profile--email a").link("mailto:"+o,o):$(".dashboard-profile--email").hide(),(o=a.twitter_username)?$(".dashboard-profile--twitter a").link("https://twitter.com/"+o,"@"+o):$(".dashboard-profile--twitter").hide()})).catch(console.error)}));