document.addEventListener("DOMContentLoaded",(function(){loadData("profile.json").then((t=>{let e,a=".dashboard-profile--";$(a+"link").href(t.html_url),$(a+"avatar").src(t.avatar_url),$(a+"name").text(t.name||t.login),$(a+"login").text("@"+t.login),$(a+"bio").text(t.bio||""),(e=t.location)?$(a+"location span").text(e):$(a+"location").hide(),(e=t.blog)?$(a+"blog a").link(e):$(a+"blog").hide(),(e=t.email)?$(a+"email a").link("mailto:"+e,e):$(a+"email").hide(),(e=t.twitter_username)?$(a+"twitter a").link("https://twitter.com/"+e,"@"+e):$(a+"twitter").hide()})).catch(console.error)}));