document.addEventListener("DOMContentLoaded",(function(){loadData("orgs.json").then((a=>{const e=$(".orgs").el;for(const i of a){const a=el("div",{className:"box profile item"});a.innerHTML=`<a href="${i.html_url}" target="_blank" class="profile-link"><img src="${i.avatar_url}" class="profile-avatar" alt="GitHub Profile Avatar" /></a><div class="profile-info"><h2 class="profile-name">${i.name}</h2><h3 class="profile-login">@${i.name}</h3><p class="profile-bio">${i.description}</p><ul class="profile-details">`+(i.location?`<li class="profile-location"><i class="fa fa-map-marker" aria-hidden="true"></i><span>${i.location}</span></li>`:"")+'<li class="profile-joined"><i class="fa fa-clock-o" aria-hidden="true"></i>'+`<span>Founded at ${fDate(i.created_at)}</span></li>`+(i.blog?`<li class="profile-blog"><i class="fa fa-link" aria-hidden="true"></i><a href="${i.blog}" target="_blank">${i.blog.replace(/^https?:\/\//,"")}</a></li>`:"")+(i.email?`<li class="profile-email"><i class="fa fa-envelope-o" aria-hidden="true"></i><a href="mailto:${i.email}" target="_blank">${i.email}</a></li>`:"")+(i.twitter_username?`<li class="profile-twitter"><i class="fa fa-twitter" aria-hidden="true"></i><a href="https://x.com/${i.twitter_username}" target="_blank">@${i.twitter_username}</a></li>`:"")+"</ul></div>",e.appendChild(a)}})).catch(console.error)}));