document.addEventListener("DOMContentLoaded",(function(){loadData("orgs.json").then((a=>{const o=$(".orgs").el;for(const e of a){const a=el("div",{className:"box profile item"});a.innerHTML=`<a href="${e.html_url}" target="_blank" class="profile-link"><img src="${e.avatar_url}" class="profile-avatar" alt="GitHub Profile Avatar" /></a><div class="dashboard-profile--info"><h2 class="profile-name">${e.name}</h2><h3 class="profile-login">@${e.name}</h3><p class="profile-bio">${e.description}</p></div>`,o.appendChild(a)}})).catch(console.error)}));