document.addEventListener("DOMContentLoaded",(function(){loadData("repos.json").then((a=>{const e=$(".repos").el;a.forEach((a=>{const s=langKey(a.language),[l,i]=a.full_name.split("/"),t=el("div",{className:`box item repo ${s}`});let c,n="cube";a.archived?(t.classList.add("archived"),n="file-archive-o",c="Archived"):a.fork?(t.classList.add("fork"),n="code-fork",c="Forked"):a.is_template?(t.classList.add("template"),n="clone",c="Template"):a.private&&(t.classList.add("private"),n="lock",c="Private"),t.innerHTML=`<div class="repo-header"><i class="fa fa-${n} repo-badge" aria-hidden="true"></i><h3 class="repo-name"><span>${l}</span>/<a href="${a.html_url}" target="_blank">${i}</a></h3>`+(c?`<span class="repo-status">${c}</span>`:"")+"</div>"+`<p class="repo-desc">${a.description}</p>`+`<ul class="repo-topics">${(a.topics??[]).slice(0,7).map((a=>`<li>${a}</li>`)).join("")}</ul><ul class="repo-meta">`+(s?`<li class="repo-lang" style="--c:var(--lang-${s})">${a.language}</li>`:"")+(a.license?.name?`<li class="repo-license"><i class="fa fa-balance-scale" aria-hidden="true"></i><span>${a.license.name}</span></li>`:"")+`<li class="repo-updated">Updated at ${fDate(a.updated_at)}</span></li></ul>`,e.appendChild(t)}))})).catch(console.error)}));