document.addEventListener("DOMContentLoaded",(function(){loadData("repos.json").then((e=>{const a=$(".repos").el;e.forEach((e=>{const s=langKey(e.language),[t,r]=e.full_name.split("/"),d=el("div",{className:`box item repo ${s}`});let l,o="cube";e.archived?(d.classList.add("archived"),o="file-archive-o",l="Archived"):e.fork?(d.classList.add("fork"),o="code-fork",l="Forked"):e.is_template?(d.classList.add("template"),o="clone",l="Template"):e.private&&(d.classList.add("private"),o="lock",l="Private"),d.innerHTML=`<div class="repo-header"><i class="fa fa-${o} repo-badge" aria-hidden="true"></i><h3 class="repo-name"><span>${t}</span>/<a href="${e.html_url}" target="_blank">${r}</a></h3>`+(l?`<span class="repo-status">${l}</span>`:"")+"</div>"+`<p class="repo-desc">${e.description}</p>`,a.appendChild(d)}))})).catch(console.error)}));