document.addEventListener("DOMContentLoaded",(function(){loadData("repos.json").then((a=>{const e=$(".repos").el;a.forEach((a=>{const s=langKey(a.language),[i,l]=a.full_name.split("/"),r=el("div",{className:`box item repo ${s}`});let t,c="cube";a.archived?(r.classList.add("archived"),c="file-archive-o",t="Archived"):a.fork?(r.classList.add("fork"),c="code-fork",t="Forked"):a.is_template?(r.classList.add("template"),c="clone",t="Template"):a.private&&(r.classList.add("private"),c="lock",t="Private"),r.innerHTML=`<div class="repo-info"><div class="repo-header"><i class="fa fa-${c} repo-badge" aria-hidden="true"></i><h3 class="repo-name"><span>${i}</span>/<a href="${a.html_url}" target="_blank">${l}</a></h3>`+(t?`<span class="repo-status">${t}</span>`:"")+"</div>"+`<p class="repo-desc">${a.description}</p>`+`<ul class="repo-topics">${(a.topics??[]).slice(0,7).map((a=>`<li>${a}</li>`)).join("")}</ul><ul class="repo-meta">`+(s?`<li class="repo-lang" style="--c:var(--lang-${s})">${a.language}</li>`:"")+(a.license?.name?`<li class="repo-license"><i class="fa fa-balance-scale" aria-hidden="true"></i><span>${a.license.name}</span></li>`:"")+(a.homepage?`<li class="repo-homepage"><i class="fa fa-link" aria-hidden="true"></i><a href="${a.homepage}" target="_blank">${a.homepage.replace(/^https?:\/\//,"")}</a></li>`:"")+`<li class="repo-size">${fSize(a.size)}</li>`+`<li class="repo-updated">Updated at ${fDate(a.updated_at)}</li></ul></div><div class="repo-stats"><div class="item"><i class="fa fa-star-o" aria-hidden="true"></i>`+`<b>${fNumber(a.stargazers_count)}</b><span>Stargazers</span></div><div class="item"><i class="fa fa-heart-o" aria-hidden="true"></i>`+`<b>${fNumber(a.subscribers_count)}</b><span>Subscribers</span></div><div class="item"><i class="fa fa-code-fork" aria-hidden="true"></i>`+`<b>${fNumber(a.forks_count)}</b><span>Forks</span></div><div class="item"><i class="fa fa-bug" aria-hidden="true"></i>`+`<b>${fNumber(a.open_issues_count)}</b><span>Issues</span></div></div>`,e.appendChild(r)}))})).catch(console.error)}));