document.addEventListener("DOMContentLoaded",(function(){loadData("repos.json").then((e=>{const a=$(".repos").el;e.forEach((e=>{const s=langKey(e.language),[l,i]=e.full_name.split("/"),t=el("div",{className:`box item repo ${s}`});let c,r="cube";e.archived?(t.classList.add("archived"),r="file-archive-o",c="Archived"):e.fork?(t.classList.add("fork"),r="code-fork",c="Forked"):e.is_template?(t.classList.add("template"),r="clone",c="Template"):e.private&&(t.classList.add("private"),r="lock",c="Private"),t.innerHTML=`<div class="repo-header"><i class="fa fa-${r} repo-badge" aria-hidden="true"></i><h3 class="repo-name"><span>${l}</span>/<a href="${e.html_url}" target="_blank">${i}</a></h3>`+(c?`<span class="repo-status">${c}</span>`:"")+"</div>"+`<p class="repo-desc">${e.description}</p>`+`<ul class="repo-topics">${(e.topics??[]).slice(0,7).map((e=>`<li>${e}</li>`)).join("")}</ul><ul class="repo-meta">`+(s?`<li class="repo-lang" style="--c:var(--lang-${s})">${e.language}</li>`:"")+(e.license?.name?`<li class="repo-license"><i class="fa fa-balance-scale" aria-hidden="true"></i><span>${e.license.name}</span></li>`:"")+(e.homepage?`<li class="repo-homepage"><i class="fa fa-link" aria-hidden="true"></i><a href="${e.homepage}" target="_blank">${e.homepage.replace(/^https?:\/\//,"")}</a></li>`:"")+`<li class="repo-size">${fSize(e.size)}</li>`+`<li class="repo-updated">Updated at ${fDate(e.updated_at)}</li></ul>`,a.appendChild(t)}))})).catch(console.error)}));