document.addEventListener("DOMContentLoaded",(function(){loadData("repos.json").then((e=>{const a=$(".repos").el;e.forEach((e=>{const s=langKey(e.language),[l,i]=e.full_name.split("/"),t=el("div",{className:`box item repo ${s}`});let o,r="cube";e.archived?(t.classList.add("archived"),r="file-archive-o",o="Archived"):e.fork?(t.classList.add("fork"),r="code-fork",o="Forked"):e.is_template?(t.classList.add("template"),r="clone",o="Template"):e.private&&(t.classList.add("private"),r="lock",o="Private"),t.innerHTML=`<div class="repo-header"><i class="fa fa-${r} repo-badge" aria-hidden="true"></i><h3 class="repo-name"><span>${l}</span>/<a href="${e.html_url}" target="_blank">${i}</a></h3>`+(o?`<span class="repo-status">${o}</span>`:"")+"</div>"+`<p class="repo-desc">${e.description}</p>`+`<ul class="repo-topics">${(e.topics??[]).slice(0,7).map((e=>`<li>${e}</li>`)).join("")}</ul><div class="repo-meta"></div>`,a.appendChild(t)}))})).catch(console.error)}));